using System.Collections;using System.Collections.Generic;using UnityEngine;public class GameMgr : Singleton<GameMgr>{    protected GameMgr() { }    public string identify = "GameMgr";    private CameraController cameraController;    private GameDirector gameDirector;    private UIController uiController;    private AudioController audioController;    private bool isInputEnable = false;    public void Init()    {        cameraController = GameObject.Find("MainCamera").GetComponent<CameraController>();        gameDirector = GameObject.Find("GameDirector").GetComponent<GameDirector>();        uiController = GameObject.Find("UICanvas").GetComponent<UIController>();        audioController = GameObject.Find("GameDirector").GetComponent<AudioController>();    }    public void PlayBGM()    {        audioController.PlayBGM();    }    public void PlayBossBGM()    {        audioController.PlayBossBGM();    }    public void PlayJumpSFX()    {        audioController.PlayJump();    }    public void PlayAttackSFX()    {        audioController.PlayAttack();    }    public void PlayBonfireSFX()    {        audioController.Stop();        audioController.PlayBonfire();    }    public void StartGame()    {        gameDirector.StartGame();    }    public void UIOnStart()    {        uiController.ShowMenuUI();        uiController.gameObject.SetActive(true);    }    public void BackToStart()    {        uiController.ShowMenuUI();        LevelMgr.Instance.QuitGame();        gameDirector.QuitGame();    }    public void ShowCutscene()    {        uiController.ShowCutsceneUI();    }    public void ShowGameUI()    {        uiController.ShowGameUI();    }    public bool IsInGame()    {        return gameDirector.IsInGame();    }    public void PlayerMonsterHitSFX()    {        audioController.PlayMonsterHit();    }    public void PlayMonsterDieSFX()    {        audioController.PlayMonsterDie();    }    public void PlayPickSFX()    {        audioController.PlayItem();    }    public void PlayPlayerHitSFX()    {        audioController.PlayPlayerHit();    }    public void PlayPlayerDieSFX()    {        audioController.PlayPlayerDie();    }    public void PlayBoss1DieSFX()
    {
        audioController.PlayBoss1Die();
    }    public void PlayerInvincibleMonsterHitSFX()    {        audioController.PlayInvincibleMonsterHit();    }    public void EnableInput()    {        isInputEnable = true;    }    public void DisableInput()    {        isInputEnable = false;    }    public bool IsInputEnable()    {        return isInputEnable;    }    public void SetCameraOrthoSize(float oSize)    {        cameraController.SetOrthoSize(oSize);    }    public void OnDemoBossDie()
    {        cameraController.OnDemoBossDie();
        audioController.Stop();
        PlayBoss1DieSFX();        StartCoroutine(EndDemoCoroutine());
    }    IEnumerator EndDemoCoroutine()    {        yield return new WaitForSeconds(4.0f);        DisableInput();        uiController.ShowDemoEndUI();    }}